<?php if(!defined('BASEPATH')) exit('No direct script allowed');class mdl_laporan_biaya extends CI_Model{		function __construct()	{		parent::__construct();	}	function getItem_periode($periode_awal,$periode_akhir)	{		$query = "SELECT   DISTINCT				jurnal.GLID,				jurnal.PO_NO,				jurnal.KAS_AWAL,				jurnal.TANGGAL,				master_akun.NAKUN,				detail_jurnal.KETERANGAN,  				detail_jurnal.AKUNID,				detail_jurnal.DEBET,				detail_jurnal.KREDIT  				FROM 				jurnal, detail_jurnal, master_akun				where				detail_jurnal.GLID = jurnal.GLID and				detail_jurnal.AKUNID  = master_akun.AKUNID and				detail_jurnal.AKUNID LIKE '5%' and 				`jurnal`.`TANGGAL` BETWEEN '" . $periode_awal . "' AND '" . $periode_akhir . "'   			order BY detail_jurnal.ID";		$this->db->flush_cache();		return $this->db->query($query);	}	function getItem_kas_awal($yearnow)	{		$query = "SELECT 					jurnal.GLID,					jurnal.PO_NO,					jurnal.KAS_AWAL,					jurnal.KETERANGAN,					jurnal.TANGGAL,					detail_jurnal.DEBET,					detail_jurnal.AKUNID,					detail_jurnal.KREDIT, 					detail_jurnal.GLID as glid_detail,					sum(detail_jurnal.DEBET) as debet, 					sum(detail_jurnal.KREDIT) as JUMLAH_KAS_AWAL 					FROM jurnal										Inner Join detail_jurnal ON jurnal.GLID = detail_jurnal.GLID					where SUBSTR(TANGGAL,1,4)=" . $yearnow . " and detail_jurnal.AKUNID LIKE '4%' group by detail_jurnal.STATUS";		$this->db->flush_cache();		return $this->db->query($query);	}		function getItemById($id)	{		$this->db->flush_cache();		$this->db->where('id_pembelian', $id);		return $this->db->get('pembelian');	}}